// Add.js
import React, { useState, useRef, useEffect } from 'react';
import Swal from 'sweetalert2';

function Add({ employees, setEmployees, setIsAdding }) {
  const [name, setName] = useState('');
  const [address, setAddress] = useState('');
  const [customerNumber, setCustomerNumber] = useState('');
  const [meterSerialNumber, setMeterSerialNumber] = useState('');
  const [email, setEmail] = useState('');
  const [salary, setSalary] = useState('');
  const [date, setDate] = useState('');

  const textInput = useRef(null);

  useEffect(() => {
    textInput.current.focus();
  }, []);

  const handleAdd = (e) => {
    e.preventDefault();
    if (!name || !address || !customerNumber || !meterSerialNumber || !email || !salary || !date) {
      return Swal.fire({
        icon: 'error',
        title: 'Error!',
        text: 'All fields are required.',
        showConfirmButton: true,
      });
    }

    const id = employees.length + 1;
    const newEmployee = {
      id,
      name,
      address,
      customerNumber,
      meterSerialNumber,
      email,
      salary,
      date,
    };
    employees.push(newEmployee);
    setEmployees(employees);
    setIsAdding(false);

    Swal.fire({
      icon: 'success',
      title: 'Added!',
      text: `${name}'s data has been Added.`,
      showConfirmButton: false,
      timer: 1500,
    });
  };

  return (
    <div className="small-container">
      <form onSubmit={handleAdd}>
        <h1>Add Employee</h1>
        <label htmlFor="name">Name</label>
        <input
          id="name"
          type="text"
          ref={textInput}
          name="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <label htmlFor="address">Address</label>
        <input
          id="address"
          type="text"
          name="address"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
        />
        <label htmlFor="customerNumber">Customer Number</label>
        <input
          id="customerNumber"
          type="text"
          name="customerNumber"
          value={customerNumber}
          onChange={(e) => setCustomerNumber(e.target.value)}
        />
        <label htmlFor="meterSerialNumber">Meter Serial Number</label>
        <input
          id="meterSerialNumber"
          type="text"
          name="meterSerialNumber"
          value={meterSerialNumber}
          onChange={(e) => setMeterSerialNumber(e.target.value)}
        />
        <label htmlFor="email">Email</label>
        {/* Add the remaining input fields as before */}
        {/* ... */}
        <div style={{ marginTop: '30px' }}>
          <input type="submit" value="Add" />
          <input
            style={{ marginLeft: '12px' }}
            className="muted-button"
            type="button"
            value="Cancel"
            onClick={() => setIsAdding(false)}
          />
        </div>
      </form>
    </div>
  );
}

export default Add;
